mapper.select:
select SID, USERID, PASSWD, USERNAME, LAST_LOGIN_TIME, LAST_IP, LOGIN_TIMES, THEME, REG_TIME, ORG_SID, SEX, BIRTHDAY, RECORD_DATE, STATE, REGISTER_TYPE, AUDIT_OPINION, AUDIT_RES, AUDIT_USERID, AUDIT_DATE, DELETED, OUT_DATE, USER_TYPE, (select WMSYS.WM_CONCAT(r.role_name) from T_SYS_USER_ROLE l, t_sys_role r where l.USER_SID = a.sid and l.role_sid= r.sid ) roles
from T_SYS_USERINFO a
username like '%'||#{username,jdbcType=VARCHAR}||'%'
order by reg_time desc
order by reg_time ${order}
order by last_login_time ${order}
